import React, { useState, useEffect } from "react";
import { Container, Form, Button, Card, Spinner, Row, Col, Alert } from "react-bootstrap";
import Swal from "sweetalert2";

// Utility for API calls
const fetchData = async (url, options) => {
    try {
        const response = await fetch(url, options);
        if (!response.ok) throw new Error("Failed to fetch data");
        return await response.json();
    } catch (error) {
        return { error: error.message || "Network error. Please try again later." };
    }
};

// Loader Component
const Loader = () => (
    <div className="text-center">
        <Spinner animation="border" />
        <p>Loading...</p>
    </div>
);

// Profile Component
export default function Profile() {
    const [userProfile, setUserProfile] = useState(null);
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [loadingProfile, setLoadingProfile] = useState(true);
    const [loadingPassword, setLoadingPassword] = useState(false);
    const [loadingImage, setLoadingImage] = useState(false);
    const [successMessage, setSuccessMessage] = useState("");
    const [error, setError] = useState(null);

    // Placeholder image for profile
    const placeholderImage = "https://via.placeholder.com/150";

    // Fetch User Profile
    useEffect(() => {
        const fetchProfile = async () => {
            const token = localStorage.getItem("token");
            const data = await fetchData("http://localhost:4000/users/details", {
                method: "GET",
                headers: { Authorization: `Bearer ${token}` },
            });

            if (data.code === "USER-FOUND") {
                setUserProfile(data.result);
            } else {
                setError(data.message || "Unable to fetch profile.");
            }
            setLoadingProfile(false);
        };

        fetchProfile();
    }, []);

    // Handle Password Update
    const handlePasswordUpdate = async (e) => {
        e.preventDefault();
        setLoadingPassword(true);

        if (newPassword !== confirmPassword) {
            setLoadingPassword(false);
            return Swal.fire({
                title: "Passwords don't match!",
                text: "Please ensure both password fields match.",
                icon: "error",
            });
        }

        const token = localStorage.getItem("token");
        const data = await fetchData("http://localhost:4000/users/update-password", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ newPassword }),
        });

        if (data.code === "PASSWORD-UPDATE-SUCCESS") {
            setSuccessMessage(data.message || "Password updated successfully!");
            setNewPassword("");
            setConfirmPassword("");
        } else {
            Swal.fire({
                title: "Error",
                text: data.message || "Unable to update password.",
                icon: "error",
            });
        }
        setLoadingPassword(false);
    };

    // Handle Profile Picture Upload
    const handleProfilePictureUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append("profilePicture", file);

        const token = localStorage.getItem("token");
        setLoadingImage(true);

        const data = await fetchData("http://localhost:4000/users/upload-profile-picture", {
            method: "POST",
            headers: { Authorization: `Bearer ${token}` },
            body: formData,
        });

        if (data.code === "PROFILE-PICTURE-UPLOAD-SUCCESS") {
            setUserProfile((prevProfile) => ({
                ...prevProfile,
                profilePicture: data.profilePicture,
            }));
            Swal.fire({
                title: "Success",
                text: "Profile picture updated successfully!",
                icon: "success",
            });
        } else {
            Swal.fire({
                title: "Error",
                text: data.message || "Unable to upload profile picture.",
                icon: "error",
            });
        }

        setLoadingImage(false);
    };

    return (
        <Container className="mt-5">
            <h2 className="text-center mb-4">Profile Settings</h2>

            <Row>
                {/* User Profile Section */}
                <Col lg={6} className="mb-4">
                    <Card className="shadow-sm">
                        <Card.Header className="bg-primary text-white">
                            <h5>Your Profile</h5>
                        </Card.Header>
                        <Card.Body>
                            {loadingProfile ? (
                                <Loader />
                            ) : error ? (
                                <Alert variant="danger">{error}</Alert>
                            ) : userProfile ? (
                                <div className="text-center">
                                    <img
                                        src={userProfile.profilePicture || placeholderImage}
                                        alt="Profile"
                                        className="rounded-circle mb-3"
                                        style={{ width: "150px", height: "150px", objectFit: "cover" }}
                                    />
                                    <Form.Group controlId="profilePictureUpload" className="mb-3">
                                        <Form.Label>Update Profile Picture</Form.Label>
                                        <Form.Control
                                            type="file"
                                            onChange={handleProfilePictureUpload}
                                            disabled={loadingImage}
                                        />
                                    </Form.Group>
                                    <p>
                                        <strong>Name:</strong> {userProfile.firstName}{" "}
                                        {userProfile.middleName} {userProfile.lastName}
                                    </p>
                                    <p>
                                        <strong>Email:</strong> {userProfile.email}
                                    </p>
                                    <p>
                                        <strong>Contact Number:</strong> {userProfile.contactNumber}
                                    </p>
                                </div>
                            ) : (
                                <Alert variant="danger">Profile information unavailable.</Alert>
                            )}
                        </Card.Body>
                    </Card>
                </Col>

                {/* Password Update Section */}
                <Col lg={6} className="mb-4">
                    <Card className="shadow-sm">
                        <Card.Header className="bg-secondary text-white">
                            <h5>Update Password</h5>
                        </Card.Header>
                        <Card.Body>
                            {successMessage && (
                                <Alert
                                    variant="success"
                                    onClose={() => setSuccessMessage("")}
                                    dismissible
                                >
                                    {successMessage}
                                </Alert>
                            )}
                            <Form onSubmit={handlePasswordUpdate}>
                                <Form.Group className="mb-3">
                                    <Form.Label>New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Enter new password"
                                        value={newPassword}
                                        onChange={(e) => setNewPassword(e.target.value)}
                                        required
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Label>Confirm New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Confirm new password"
                                        value={confirmPassword}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                        required
                                    />
                                </Form.Group>

                                <Button
                                    variant="secondary"
                                    type="submit"
                                    className="w-100"
                                    disabled={loadingPassword || !newPassword || !confirmPassword}
                                >
                                    {loadingPassword ? (
                                        <Spinner size="sm" animation="border" />
                                    ) : (
                                        "Update Password"
                                    )}
                                </Button>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
