import React, { useState, useEffect } from "react";
import { Container, Row, Col, Card, Button, Form, Modal, Alert } from "react-bootstrap";
import Swal from "sweetalert2";

export default function Profile() {
    const [userProfile, setUserProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState(false);
    const [profilePicture, setProfilePicture] = useState(null);
    const [preview, setPreview] = useState(null);
    const [showPasswordModal, setShowPasswordModal] = useState(false);
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [darkMode, setDarkMode] = useState(false);

    // Fetch user profile data
    useEffect(() => {
        const token = localStorage.getItem("token");
        fetch("http://localhost:4000/users/details", {
            method: "GET",
            headers: { Authorization: `Bearer ${token}` },
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.code === "USER-FOUND") setUserProfile(data.result);
                else Swal.fire("Error", data.message, "error");
            })
            .catch(() => Swal.fire("Error", "Unable to fetch profile", "error"))
            .finally(() => setLoading(false));
    }, []);

    // Handle profile picture upload
    const handlePictureUpload = (e) => {
        const file = e.target.files[0];
        setProfilePicture(file);
        setPreview(URL.createObjectURL(file));
    };

    // Save updated profile details
    const saveProfile = () => {
        const token = localStorage.getItem("token");

        const formData = new FormData();
        formData.append("profilePicture", profilePicture);
        formData.append("firstName", userProfile.firstName);
        formData.append("lastName", userProfile.lastName);
        formData.append("email", userProfile.email);
        formData.append("contactNumber", userProfile.contactNumber);

        fetch("http://localhost:4000/users/update-profile", {
            method: "PUT",
            headers: { Authorization: `Bearer ${token}` },
            body: formData,
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.code === "PROFILE-UPDATE-SUCCESS") {
                    Swal.fire("Success", "Profile updated successfully!", "success");
                    setEditing(false);
                } else {
                    Swal.fire("Error", data.message, "error");
                }
            })
            .catch(() => Swal.fire("Error", "Unable to update profile", "error"));
    };

    // Handle password update
    const handlePasswordUpdate = () => {
        if (newPassword !== confirmPassword) {
            Swal.fire("Error", "Passwords do not match!", "error");
            return;
        }

        const token = localStorage.getItem("token");

        fetch("http://localhost:4000/users/update-password", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ newPassword }),
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.code === "PASSWORD-UPDATE-SUCCESS") {
                    Swal.fire("Success", "Password updated successfully!", "success");
                    setShowPasswordModal(false);
                } else {
                    Swal.fire("Error", data.message, "error");
                }
            })
            .catch(() => Swal.fire("Error", "Unable to update password", "error"));
    };

    // Mock activity logs
    const activityLogs = [
        { id: 1, action: "Logged in", date: "2024-12-01 10:00 AM" },
        { id: 2, action: "Updated password", date: "2024-11-29 5:30 PM" },
        { id: 3, action: "Uploaded profile picture", date: "2024-11-28 8:45 AM" },
    ];

    // Toggle dark mode
    const toggleDarkMode = () => setDarkMode((prev) => !prev);

    return (
        <Container className={`mt-5 ${darkMode ? "bg-dark text-light" : ""}`}>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Profile Settings</h2>
                <Button variant="secondary" onClick={toggleDarkMode}>
                    {darkMode ? "Light Mode" : "Dark Mode"}
                </Button>
            </div>
            <Row>
                {/* Profile Details */}
                <Col md={6}>
                    <Card className="mb-4 shadow-sm">
                        <Card.Header>Profile Details</Card.Header>
                        <Card.Body>
                            {loading ? (
                                <p>Loading...</p>
                            ) : userProfile ? (
                                <div>
                                    <Form.Group>
                                        <Form.Label>Profile Picture</Form.Label>
                                        <div className="d-flex align-items-center mb-3">
                                            <img
                                                src={preview || userProfile.profilePicture || "default-avatar.png"}
                                                alt="Profile"
                                                className="rounded-circle"
                                                style={{ width: "80px", height: "80px", objectFit: "cover", marginRight: "15px" }}
                                            />
                                            <Form.Control
                                                type="file"
                                                accept="image/*"
                                                onChange={handlePictureUpload}
                                                disabled={!editing}
                                            />
                                        </div>
                                    </Form.Group>
                                    <Form>
                                        <Form.Group className="mb-3">
                                            <Form.Label>First Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                value={userProfile.firstName}
                                                onChange={(e) =>
                                                    setUserProfile({ ...userProfile, firstName: e.target.value })
                                                }
                                                disabled={!editing}
                                            />
                                        </Form.Group>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Last Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                value={userProfile.lastName}
                                                onChange={(e) =>
                                                    setUserProfile({ ...userProfile, lastName: e.target.value })
                                                }
                                                disabled={!editing}
                                            />
                                        </Form.Group>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Email</Form.Label>
                                            <Form.Control
                                                type="email"
                                                value={userProfile.email}
                                                onChange={(e) =>
                                                    setUserProfile({ ...userProfile, email: e.target.value })
                                                }
                                                disabled={!editing}
                                            />
                                        </Form.Group>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Contact Number</Form.Label>
                                            <Form.Control
                                                type="text"
                                                value={userProfile.contactNumber}
                                                onChange={(e) =>
                                                    setUserProfile({ ...userProfile, contactNumber: e.target.value })
                                                }
                                                disabled={!editing}
                                            />
                                        </Form.Group>
                                        {editing ? (
                                            <Button variant="success" onClick={saveProfile}>
                                                Save Changes
                                            </Button>
                                        ) : (
                                            <Button variant="primary" onClick={() => setEditing(true)}>
                                                Edit Profile
                                            </Button>
                                        )}
                                    </Form>
                                </div>
                            ) : (
                                <p className="text-danger">Failed to load profile.</p>
                            )}
                        </Card.Body>
                    </Card>
                </Col>

                {/* Activity Logs */}
                <Col md={6}>
                    <Card className="mb-4 shadow-sm">
                        <Card.Header>Activity Logs</Card.Header>
                        <Card.Body>
                            {activityLogs.map((log) => (
                                <p key={log.id}>
                                    <strong>{log.action}</strong> - {log.date}
                                </p>
                            ))}
                            <Button variant="danger" onClick={() => setShowPasswordModal(true)}>
                                Update Password
                            </Button>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            {/* Password Update Modal */}
            <Modal show={showPasswordModal} onHide={() => setShowPasswordModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Update Password</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>New Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Enter new password"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Confirm New Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Confirm new password"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                            />
                        </Form.Group>
                        <Button variant="primary" onClick={handlePasswordUpdate}>
                            Save Password
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </Container>
    );
}
