import React, { useState, useEffect } from "react";
import { Container, Form, Button, Card, Spinner } from "react-bootstrap";
import Swal from "sweetalert2";

// Utility for API calls
const fetchData = async (url, options) => {
    try {
        const response = await fetch(url, options);
        return await response.json();
    } catch (error) {
        return { error: "Network error. Please try again later." };
    }
};

// Profile Component
export default function Profile() {
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [userProfile, setUserProfile] = useState(null);
    const [loading, setLoading] = useState(true);

    // Fetch User Profile
    useEffect(() => {
        const fetchProfile = async () => {
            const token = localStorage.getItem("token");
            const data = await fetchData("http://localhost:4000/users/details", {
                method: "GET",
                headers: { Authorization: `Bearer ${token}` },
            });

            if (data.code === "USER-FOUND") {
                setUserProfile(data.result);
            } else {
                Swal.fire({
                    title: "Error",
                    text: data.message || "Unable to fetch profile.",
                    icon: "error",
                });
            }
            setLoading(false);
        };

        fetchProfile();
    }, []);

    // Handle Password Update
    const handlePasswordUpdate = async (e) => {
        e.preventDefault();

        if (newPassword !== confirmPassword) {
            return Swal.fire({
                title: "Passwords don't match!",
                text: "Please ensure both passwords match.",
                icon: "error",
            });
        }

        const token = localStorage.getItem("token");
        const data = await fetchData("http://localhost:4000/users/update-password", {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ newPassword, confirmPassword }),
        });

        Swal.fire({
            title: data.code === "PASSWORD-UPDATE-SUCCESS" ? "Success" : "Error",
            text: data.message || "Unable to update password.",
            icon: data.code === "PASSWORD-UPDATE-SUCCESS" ? "success" : "error",
        });

        if (data.code === "PASSWORD-UPDATE-SUCCESS") {
            setNewPassword("");
            setConfirmPassword("");
        }
    };

    // Render Profile Information
    const ProfileDetails = () => {
        if (loading) {
            return <Spinner animation="border" />;
        }

        if (!userProfile) {
            return <p>Unable to load profile information.</p>;
        }

        return (
            <div>
                <h3 className="mt-5 mb-3">Your Information</h3>
                <p>
                    <strong>Name:</strong> {userProfile.firstName} {userProfile.middleName} {userProfile.lastName}
                </p>
                <p>
                    <strong>Email:</strong> {userProfile.email}
                </p>
                <p>
                    <strong>Contact Number:</strong> {userProfile.contactNumber}
                </p>
            </div>
        );
    };

    return (
        <Container className="mt-5">
            <h2>Profile Settings</h2>

            <Card className="mt-4">
                <Card.Body>
                    <ProfileDetails />
                </Card.Body>
            </Card>

            <Card className="mt-4">
                <Card.Body>
                    <Form onSubmit={handlePasswordUpdate}>
                        <Form.Group className="mb-3 mt-5">
                            <Form.Label>New Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Enter new password"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Confirm New Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Confirm new password"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit" disabled={loading}>
                            {loading ? <Spinner size="sm" animation="border" /> : "Update Password"}
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
}
